CREATE TABLE "OWN_ADDR" (
	"OA_IDX"	int   GENERATED BY DEFAULT AS IDENTITY		NOT NULL,
	"RES_DATE"	date		NULL,
	"RES_REGN_CODE"	varchar(5)		NULL,
	"BUYER_REGN_CODE"	varchar(5)		NULL,
	"TOT"	int		NULL
);

COMMENT ON COLUMN "OWN_ADDR"."RES_DATE" IS '등기이전일자';

COMMENT ON COLUMN "OWN_ADDR"."RES_REGN_CODE" IS '결과지역코드';

COMMENT ON COLUMN "OWN_ADDR"."BUYER_REGN_CODE" IS '매수자주소 지역코드';

COMMENT ON COLUMN "OWN_ADDR"."TOT" IS '결과건수';

CREATE TABLE "OWN_SEX_AGE" (
	"OSA_IDX"	int	  GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"RES_DATE"	date		NULL,
	"RES_REGN_CODE"	varchar(5)		NULL,
	"BUYER_SEX"	varchar(10)		NULL,
	"BUYER_AGES"	varchar(30)		NULL,
	"TOT"	int		NULL
);

COMMENT ON COLUMN "OWN_SEX_AGE"."RES_DATE" IS '등기이전일자';

COMMENT ON COLUMN "OWN_SEX_AGE"."RES_REGN_CODE" IS '결과지역코드';

COMMENT ON COLUMN "OWN_SEX_AGE"."BUYER_SEX" IS '매수자 성별';

COMMENT ON COLUMN "OWN_SEX_AGE"."BUYER_AGES" IS '매수자 나이대';

COMMENT ON COLUMN "OWN_SEX_AGE"."TOT" IS '결과건수';

CREATE TABLE "OWN_TYPE" (
	"OT_IDX"	int	  GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"RES_DATE"	date		NULL,
	"RES_REGN_CODE"	varchar(5)		NULL,
	"TOT"	int		NULL,
	"OWNER_CLS"	varchar(30)		NULL
);

COMMENT ON COLUMN "OWN_TYPE"."RES_DATE" IS '등기이전일자';

COMMENT ON COLUMN "OWN_TYPE"."RES_REGN_CODE" IS '결과지역코드';

COMMENT ON COLUMN "OWN_TYPE"."TOT" IS '결과건수';

COMMENT ON COLUMN "OWN_TYPE"."OWNER_CLS" IS '소유유형(내국인/법인...)';

CREATE TABLE "OWN_FOREIGNER" (
	"OF_IDX"	int	  GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"RES_DATE"	date		NULL,
	"RES_REGN_CODE"	varchar(5)		NULL,
	"BUYER_NATION"	varchar(30)		NULL,
	"TOT"	int		NULL
);

COMMENT ON COLUMN "OWN_FOREIGNER"."RES_DATE" IS '등기이전일자';

COMMENT ON COLUMN "OWN_FOREIGNER"."RES_REGN_CODE" IS '결과지역코드';

COMMENT ON COLUMN "OWN_FOREIGNER"."BUYER_NATION" IS '매수자 국적';

COMMENT ON COLUMN "OWN_FOREIGNER"."TOT" IS '결과건수';

CREATE TABLE "REALESTATE_OWN" (
	"RO_IDX"	int GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"RES_DATE"	varchar(10)		NULL,
	"REGN_CODE"	varchar(5)		NULL,
	"PROPERTY_TYPE"	varchar(30)		NULL,
	"PURPOSE"	varchar(30)		NULL,
	"OWNER_CLS"	varchar(30)		NULL,
	"OWNER_SEX"	varchar(10)		NULL,
	"OWNER_AGES"	varchar(30)		NULL,
	"TOT"	int		NULL
);

COMMENT ON COLUMN "REALESTATE_OWN"."RES_DATE" IS '접수년월';

COMMENT ON COLUMN "REALESTATE_OWN"."REGN_CODE" IS '지역코드';

COMMENT ON COLUMN "REALESTATE_OWN"."PROPERTY_TYPE" IS '부동산구분';

COMMENT ON COLUMN "REALESTATE_OWN"."PURPOSE" IS '등기목적';

COMMENT ON COLUMN "REALESTATE_OWN"."OWNER_CLS" IS '소유유형(내국인/법인..)';

COMMENT ON COLUMN "REALESTATE_OWN"."OWNER_SEX" IS '성별(남/여/기타)';

COMMENT ON COLUMN "REALESTATE_OWN"."OWNER_AGES" IS '연령대';

COMMENT ON COLUMN "REALESTATE_OWN"."TOT" IS '명의인수';

CREATE TABLE "REAL_PRC_APT" (
	"RPA_IDX"	int	GENERATED BY DEFAULT AS IDENTITY	NOT NULL,
	"AMOUNT"	int		NULL,
	"RES_DATE"	date		NULL,
	"AREA"	float		NULL,
	"REGN_CODE"	varchar(5)		NULL
);

COMMENT ON COLUMN "REAL_PRC_APT"."AMOUNT" IS '거래금액(만원)';

COMMENT ON COLUMN "REAL_PRC_APT"."RES_DATE" IS '계약날짜';

COMMENT ON COLUMN "REAL_PRC_APT"."AREA" IS '전용면적';

COMMENT ON COLUMN "REAL_PRC_APT"."REGN_CODE" IS '지역코드';

CREATE TABLE "LOC" (
	"LOC_CODE"	varchar(5)		NOT NULL,
	"SIDO_CODE"	varchar(2)		NULL,
	"SIGUNGU_CODE"	varchar(3)		NULL,
	"SIDO"	varchar(10)		NULL,
	"SIGUNGU"	varchar(30)		NULL
);

COMMENT ON COLUMN "LOC"."LOC_CODE" IS '지역코드';

COMMENT ON COLUMN "LOC"."SIDO_CODE" IS '시도코드';

COMMENT ON COLUMN "LOC"."SIGUNGU_CODE" IS '시군구코드';

COMMENT ON COLUMN "LOC"."SIDO" IS '시도명';

COMMENT ON COLUMN "LOC"."SIGUNGU" IS '시군구명';

ALTER TABLE "OWN_ADDR" ADD CONSTRAINT "PK_OWN_ADDR" PRIMARY KEY (
	"OA_IDX"
);

ALTER TABLE "OWN_SEX_AGE" ADD CONSTRAINT "PK_OWN_SEX_AGE" PRIMARY KEY (
	"OSA_IDX"
);

ALTER TABLE "OWN_TYPE" ADD CONSTRAINT "PK_OWN_TYPE" PRIMARY KEY (
	"OT_IDX"
);

ALTER TABLE "OWN_FOREIGNER" ADD CONSTRAINT "PK_OWN_FOREIGNER" PRIMARY KEY (
	"OF_IDX"
);

ALTER TABLE "REALESTATE_OWN" ADD CONSTRAINT "PK_REALESTATE_OWN" PRIMARY KEY (
	"RO_IDX"
);

ALTER TABLE "REAL_PRC_APT" ADD CONSTRAINT "PK_REAL_PRC_APT" PRIMARY KEY (
	"RPA_IDX"
);

