	"LOC_CODE"
);

ALTER TABLE "REAL_PRC_APT" ADD CONSTRAINT "FK_REAL_PRC_APT_TO_LOC1" FOREIGN KEY ("REGN_CODE") REFERENCES "LOC" ("LOC_CODE");

