ALTER TABLE "OWN_FOREIGNER" ADD CONSTRAINT "FK_OWN_FOREIGNER_TO_LOC1" FOREIGN KEY ("RES_REGN_CODE") REFERENCES "LOC" ("LOC_CODE");

ALTER TABLE "OWN_TYPE" ADD CONSTRAINT "FK_OWN_TYPE_TO_LOC1" FOREIGN KEY ("RES_REGN_CODE") REFERENCES "LOC" ("LOC_CODE");

ALTER TABLE "OWN_SEX_AGE" ADD CONSTRAINT "FK_OWN_SEX_AGE_TO_LOC1" FOREIGN KEY ("RES_REGN_CODE") REFERENCES "LOC" ("LOC_CODE");

ALTER TABLE "OWN_ADDR" ADD CONSTRAINT "FK_OWN_ADDR_TO_LOC1" FOREIGN KEY ("RES_REGN_CODE") REFERENCES "LOC" ("LOC_CODE");

ALTER TABLE "LOC" modify (SIDO varchar2(30));

ALTER TABLE "REALESTATE_OWN" modify (OWNER_CLS varchar2(50));
